var link=document.querySelector(".send"),popup=document.querySelector(".modal"),close=popup.querySelector(".modal__close");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal__show")}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal__show")}),window.addEventListener("keydown",function(e){27===e.keyCode&&popup.classList.contains("modal__show")&&popup.classList.remove("modal__show")});